properties.scriptContent:
param(
    [string]$KeyVaultName = $env:KEY_VAULT_NAME,
    [string]$FunctionAppUrl = $env:FUNCTION_APP_URL,
    [string]$TenantId = $env:TENANT_ID
)

Write-Host 'üöÄ Starting SharePoint Contact Sync App Registration...' -ForegroundColor Green
Write-Host "Key Vault: $KeyVaultName" -ForegroundColor Cyan
Write-Host "Function App: $FunctionAppUrl" -ForegroundColor Cyan
Write-Host "Tenant ID: $TenantId" -ForegroundColor Cyan

try {
    # Wait for RBAC propagation
    Write-Host 'Waiting for RBAC permissions to propagate...' -ForegroundColor Yellow
    Start-Sleep -Seconds 30

    # Install required modules with retries
    Write-Host 'Installing Microsoft Graph PowerShell modules...' -ForegroundColor Cyan

    $maxRetries = 3
    $retryCount = 0

    do {
        try {
            Install-Module Microsoft.Graph.Authentication -Force -Scope CurrentUser -AllowClobber -Repository PSGallery
            Install-Module Microsoft.Graph.Applications -Force -Scope CurrentUser -AllowClobber -Repository PSGallery
            break
        } catch {
            $retryCount++
            Write-Warning "Module installation attempt $retryCount failed: $($_.Exception.Message)"
            if ($retryCount -lt $maxRetries) {
                Start-Sleep -Seconds 10
            } else {
                throw "Failed to install modules after $maxRetries attempts"
            }
        }
    } while ($retryCount -lt $maxRetries)

    Write-Host '‚úÖ PowerShell modules installed successfully' -ForegroundColor Green

    # Connect to Microsoft Graph using Managed Identity
    Write-Host 'Connecting to Microsoft Graph with Managed Identity...' -ForegroundColor Cyan

    try {
        Connect-MgGraph -Identity -NoWelcome
        $context = Get-MgContext
        Write-Host "‚úÖ Connected to Microsoft Graph - Tenant: $($context.TenantId)" -ForegroundColor Green
    } catch {
        Write-Error "Failed to connect to Microsoft Graph: $($_.Exception.Message)"
        throw
    }

    # Check if app already exists
    Write-Host 'Checking for existing App Registration...' -ForegroundColor Cyan
    $existingApp = Get-MgApplication -Filter "displayName eq 'SharePoint Contact Sync'" -ErrorAction SilentlyContinue

    if ($existingApp) {
        Write-Host "‚ö†Ô∏è  App Registration 'SharePoint Contact Sync' already exists" -ForegroundColor Yellow
        $app = $existingApp

        # Clean up old passwords
        Write-Host 'Removing old client secrets...' -ForegroundColor Yellow
        $oldPasswords = Get-MgApplicationPassword -ApplicationId $app.Id -ErrorAction SilentlyContinue
        foreach ($pwd in $oldPasswords) {
            try {
                Remove-MgApplicationPassword -ApplicationId $app.Id -KeyId $pwd.KeyId
                Write-Host "Removed old secret: $($pwd.DisplayName)" -ForegroundColor Yellow
            } catch {
                Write-Warning "Could not remove old secret: $($_.Exception.Message)"
            }
        }
    } else {
        Write-Host 'Creating new App Registration...' -ForegroundColor Cyan

        # Create App Registration with required permissions
        $appParams = @{
            DisplayName = 'SharePoint Contact Sync'
            RequiredResourceAccess = @(
                @{
                    # Microsoft Graph
                    ResourceAppId = '00000003-0000-0000-c000-000000000000'
                    ResourceAccess = @(
                        @{ Id = 'df021288-bdef-4463-88db-98f22de89214'; Type = 'Role' }, # User.Read.All
                        @{ Id = '205e70e5-aba6-4c52-a976-6d2d46c48043'; Type = 'Role' }, # Sites.Read.All
                        @{ Id = '9492366f-7969-46a4-8d15-ed1a20078fff'; Type = 'Role' }, # Sites.Selected
                        @{ Id = 'd13f72ca-a275-4b96-b789-48ebcc4da984'; Type = 'Role' }  # Contacts.ReadWrite
                    )
                }
            )
        }

        $app = New-MgApplication @appParams
        Write-Host "‚úÖ App Registration created with ID: $($app.AppId)" -ForegroundColor Green
    }

    # Create Service Principal if it doesn't exist
    Write-Host 'Checking for Service Principal...' -ForegroundColor Cyan
    $servicePrincipal = Get-MgServicePrincipal -Filter "appId eq '$($app.AppId)'" -ErrorAction SilentlyContinue

    if (-not $servicePrincipal) {
        Write-Host 'Creating Service Principal...' -ForegroundColor Cyan
        $servicePrincipal = New-MgServicePrincipal -AppId $app.AppId
        Write-Host "‚úÖ Service Principal created with ID: $($servicePrincipal.Id)" -ForegroundColor Green
    } else {
        Write-Host "‚úÖ Service Principal already exists: $($servicePrincipal.Id)" -ForegroundColor Green
    }

    # Create new client secret
    Write-Host 'Creating new client secret...' -ForegroundColor Cyan
    $passwordCredential = @{
        DisplayName = 'SharePoint Contact Sync Secret'
        EndDateTime = (Get-Date).AddYears(2)
    }

    $secret = Add-MgApplicationPassword -ApplicationId $app.Id -PasswordCredential $passwordCredential
    Write-Host "‚úÖ Client secret created (expires: $($secret.EndDateTime))" -ForegroundColor Green

    # Connect to Azure for Key Vault operations
    Write-Host 'Connecting to Azure for Key Vault access...' -ForegroundColor Cyan
    Connect-AzAccount -Identity
    Write-Host '‚úÖ Connected to Azure' -ForegroundColor Green

    # Store credentials in Key Vault
    Write-Host "Storing credentials in Key Vault: $KeyVaultName" -ForegroundColor Cyan

    Set-AzKeyVaultSecret -VaultName $KeyVaultName -Name 'ClientId' -SecretValue (ConvertTo-SecureString $app.AppId -AsPlainText -Force)
    Set-AzKeyVaultSecret -VaultName $KeyVaultName -Name 'ClientSecret' -SecretValue (ConvertTo-SecureString $secret.SecretText -AsPlainText -Force)
    Set-AzKeyVaultSecret -VaultName $KeyVaultName -Name 'TenantId' -SecretValue (ConvertTo-SecureString $TenantId -AsPlainText -Force)

    Write-Host '‚úÖ Credentials stored in Key Vault' -ForegroundColor Green

    # Generate URLs
    $adminConsentUrl = "https://login.microsoftonline.com/$TenantId/adminconsent?client_id=$($app.AppId)"
    $setupUrl = "$FunctionAppUrl/api/setup"

    Set-AzKeyVaultSecret -VaultName $KeyVaultName -Name 'AdminConsentUrl' -SecretValue (ConvertTo-SecureString $adminConsentUrl -AsPlainText -Force)
    Set-AzKeyVaultSecret -VaultName $KeyVaultName -Name 'SetupUrl' -SecretValue (ConvertTo-SecureString $setupUrl -AsPlainText -Force)

    # Create default SharePoint configuration
    $defaultConfig = @{
        sharepoint_configs = @()
        global_settings = @{
            setup_completed = $false
            default_sync_frequency = 'hourly'
            max_contacts_per_user = 1000
            enable_delta_sync = $true
            contact_category = 'SharePoint Contacts'
        }
    } | ConvertTo-Json -Depth 10 -Compress

    Set-AzKeyVaultSecret -VaultName $KeyVaultName -Name 'SharePointConfig' -SecretValue (ConvertTo-SecureString $defaultConfig -AsPlainText -Force)

    Write-Host '‚úÖ Default configuration stored' -ForegroundColor Green

    # Set deployment script outputs
    $DeploymentScriptOutputs = @{
        status = 'success'
        appId = $app.AppId
        tenantId = $TenantId
        servicePrincipalId = $servicePrincipal.Id
        adminConsentUrl = $adminConsentUrl
        setupUrl = $setupUrl
        message = 'App Registration created successfully. Admin consent required before use.'
    }

    Write-Host '' -ForegroundColor White
    Write-Host 'üéâ App Registration setup completed successfully!' -ForegroundColor Green
    Write-Host '' -ForegroundColor White
    Write-Host 'üìã Next Steps:' -ForegroundColor Cyan
    Write-Host "1. Admin Consent: $adminConsentUrl" -ForegroundColor White
    Write-Host "2. SharePoint Setup: $setupUrl" -ForegroundColor White
    Write-Host "3. Test Sync: $FunctionAppUrl/api/SharePointSync" -ForegroundColor White
    Write-Host '' -ForegroundColor White

} catch {
    Write-Error "‚ùå App Registration setup failed: $($_.Exception.Message)"
    Write-Host "Stack Trace: $($_.ScriptStackTrace)" -ForegroundColor Red

    $DeploymentScriptOutputs = @{
        status = 'error'
        error = $_.Exception.Message
        stackTrace = $_.ScriptStackTrace
    }
}
