param([string]$ManagedIdentityObjectId = $env:MANAGED_IDENTITY_OBJECT_ID, [string]$TenantId = $env:TENANT_ID);
Write-Host 'Setting Graph API Permissions for Managed Identity';
try {
  Connect-AzAccount -Identity;
  $context = Get-AzContext;
  Write-Host "Connected: $($context.Account.Id)";

  # Get access token for Microsoft Graph
  $token = [Microsoft.Azure.Commands.Common.Authentication.AzureSession]::Instance.AuthenticationFactory.Authenticate($context.Account, $context.Environment, $TenantId, $null, 'https://graph.microsoft.com/', $null).AccessToken;
  Write-Host 'Got Graph API token';

  # Headers for Graph API
  $headers = @{
    'Authorization' = "Bearer $token"
    'Content-Type' = 'application/json'
  };

  # Microsoft Graph Service Principal
  $graphAppId = '00000003-0000-0000-c000-000000000000';
  $graphSpUri = "https://graph.microsoft.com/v1.0/servicePrincipals?`$filter=appId eq '$graphAppId'";
  $graphSp = (Invoke-RestMethod -Uri $graphSpUri -Headers $headers -Method GET).value[0];
  Write-Host "Found Graph SP: $($graphSp.id)";

  # Required permissions
  $requiredPermissions = @(
    'Application.ReadWrite.All',
    'AppRoleAssignment.ReadWrite.All'
  );

  foreach ($permission in $requiredPermissions) {
    $appRole = $graphSp.appRoles | Where-Object { $_.value -eq $permission };
    if ($appRole) {
      $assignmentBody = @{
        principalId = $ManagedIdentityObjectId
        resourceId = $graphSp.id
        appRoleId = $appRole.id
      } | ConvertTo-Json;

      $assignmentUri = 'https://graph.microsoft.com/v1.0/servicePrincipals/{0}/appRoleAssignments' -f $ManagedIdentityObjectId;

      try {
        Invoke-RestMethod -Uri $assignmentUri -Headers $headers -Method POST -Body $assignmentBody;
        Write-Host "‚úì Assigned: $permission";
      } catch {
        if ($_.Exception.Response.StatusCode -eq 409) {
          Write-Host "! Already assigned: $permission";
        } else {
          Write-Warning "Failed to assign $permission : $($_.Exception.Message)";
        }
      }
    } else {
      Write-Warning "Permission not found: $permission";
    }
  };

  Write-Host 'Graph API permissions configured';
  $DeploymentScriptOutputs = @{ status = 'success'; message = 'Permissions set successfully' };

} catch {
  Write-Error "Failed: $($_.Exception.Message)";
  $DeploymentScriptOutputs = @{ status = 'error'; error = $_.Exception.Message };
}

param([string]$KeyVaultName = $env:KEY_VAULT_NAME, [string]$FunctionAppUrl = $env:FUNCTION_APP_URL, [string]$TenantId = $env:TENANT_ID);
Write-Host 'REST API App Registration Script';
try {
  Connect-AzAccount -Identity;
  $context = Get-AzContext;
  Write-Host "Connected: $($context.Account.Id)";

  # Wait a bit for permissions to propagate
  Start-Sleep -Seconds 30;

  # Get access token for Microsoft Graph
  $token = [Microsoft.Azure.Commands.Common.Authentication.AzureSession]::Instance.AuthenticationFactory.Authenticate($context.Account, $context.Environment, $TenantId, $null, 'https://graph.microsoft.com/', $null).AccessToken;
  Write-Host 'Got Graph API token';

  # Headers for Graph API
  $headers = @{
    'Authorization' = "Bearer $token"
    'Content-Type' = 'application/json'
  };

  # Delete existing apps via REST API
  $searchUri = "https://graph.microsoft.com/v1.0/applications?`$filter=displayName eq 'SharePoint Contact Sync'";
  $existingApps = Invoke-RestMethod -Uri $searchUri -Headers $headers -Method GET;
  foreach ($app in $existingApps.value) {
    $deleteUri = "https://graph.microsoft.com/v1.0/applications/$($app.id)";
    Invoke-RestMethod -Uri $deleteUri -Headers $headers -Method DELETE;
    Write-Host "Deleted app: $($app.appId)";
  };

  # Create new app via REST API
  $appBody = @{
    displayName = 'SharePoint Contact Sync'
    requiredResourceAccess = @(
      @{
        resourceAppId = '00000003-0000-0000-c000-000000000000'
        resourceAccess = @(
          @{ id = '332a536c-c7ef-4017-ab91-336970924f0d'; type = 'Role' },
          @{ id = 'd56682ec-c09e-4743-aaf4-1a3aac4caa21'; type = 'Role' },
          @{ id = 'df021288-bdef-4463-88db-98f22de89214'; type = 'Role' }
        )
      }
    )
    web = @{
      redirectUris = @('https://login.microsoftonline.com/common/oauth2/nativeclient')
    }
  } | ConvertTo-Json -Depth 10;

  $createUri = 'https://graph.microsoft.com/v1.0/applications';
  $newApp = Invoke-RestMethod -Uri $createUri -Headers $headers -Method POST -Body $appBody;
  Write-Host "Created app: $($newApp.appId)";

  # Create service principal
  $spBody = @{ appId = $newApp.appId } | ConvertTo-Json;
  $spUri = 'https://graph.microsoft.com/v1.0/servicePrincipals';
  $sp = Invoke-RestMethod -Uri $spUri -Headers $headers -Method POST -Body $spBody;
  Write-Host "Created SP: $($sp.id)";

  # Create client secret
  $secretBody = @{
    passwordCredential = @{
      displayName = 'SharePoint Contact Sync Secret'
      endDateTime = (Get-Date).AddYears(2).ToString('yyyy-MM-ddTHH:mm:ss.fffZ')
    }
  } | ConvertTo-Json -Depth 5;

  $secretUri = "https://graph.microsoft.com/v1.0/applications/$($newApp.id)/addPassword";
  $secret = Invoke-RestMethod -Uri $secretUri -Headers $headers -Method POST -Body $secretUri;
  Write-Host 'Created client secret';

  # Store in Key Vault
  Set-AzKeyVaultSecret -VaultName $KeyVaultName -Name 'ClientId' -SecretValue (ConvertTo-SecureString $newApp.appId -AsPlainText -Force);
  Set-AzKeyVaultSecret -VaultName $KeyVaultName -Name 'ClientSecret' -SecretValue (ConvertTo-SecureString $secret.secretText -AsPlainText -Force);
  Set-AzKeyVaultSecret -VaultName $KeyVaultName -Name 'TenantId' -SecretValue (ConvertTo-SecureString $TenantId -AsPlainText -Force);

  $adminConsentUrl = "https://login.microsoftonline.com/$TenantId/adminconsent?client_id=$($newApp.appId)";
  Set-AzKeyVaultSecret -VaultName $KeyVaultName -Name 'AdminConsentUrl' -SecretValue (ConvertTo-SecureString $adminConsentUrl -AsPlainText -Force);
  Set-AzKeyVaultSecret -VaultName $KeyVaultName -Name 'SetupUrl' -SecretValue (ConvertTo-SecureString "$FunctionAppUrl/api/setup" -AsPlainText -Force);

  $config = @{sharepoint_configs=@();global_settings=@{setup_completed=$false}} | ConvertTo-Json -Depth 10;
  Set-AzKeyVaultSecret -VaultName $KeyVaultName -Name 'SharePointConfig' -SecretValue (ConvertTo-SecureString $config -AsPlainText -Force);

  $DeploymentScriptOutputs = @{
    status = 'success'
    appId = $newApp.appId
    adminConsentUrl = $adminConsentUrl
    setupUrl = "$FunctionAppUrl/api/setup"
    message = 'App Registration created via REST API'
  };

  Write-Host "SUCCESS: App $($newApp.appId) created";
  Write-Host "Admin Consent: $adminConsentUrl";

} catch {
  Write-Error "Failed: $($_.Exception.Message)";
  $DeploymentScriptOutputs = @{
    status = 'error'
    error = $_.Exception.Message
  };
}
